mapper.select:
SELECT R1.* FROM ( SELECT CMS_CD , BRD_NO , TITLE , CONT , HIT , DEP , SECRET_YN , REP_YN , ATTACH_ID , REG_ID , fn_getMemberNm(REG_ID) AS REG_NM , DATE_FORMAT(REG_DT,'%Y-%m-%d') AS REG_DT FROM CMS_CONT_BRD WHERE CMS_CD = #{cmsCd} AND DEL_YN = 'N' AND UP_BRD_NO IS NULL
AND SECRET_YN = 'Y'
) R1 WHERE 1=1
AND ( TITLE LIKE CONCAT('%', #{searchValue}, '%') OR CONT LIKE CONCAT('%', #{searchValue}, '%') )
AND ${searchKey} LIKE CONCAT('%', #{searchValue}, '%')
ORDER BY BRD_NO DESC LIMIT #{startRow}, #{pageSize}

SELECT CMS_CD , BRD_NO , UP_BRD_NO , TITLE , CONT , HIT , DEP , SECRET_YN , REP_YN , ATTACH_ID , REG_ID
, fn_getEmpCodeNm(REG_ID) AS REG_NM , IFNULL(FN_GetDeptCodeNm((SELECT B.ORGNZT_ID FROM COMTNEMPLYRINFO B WHERE REG_ID = B.EMPLYR_ID)), '') AS REG_OFF_NM , IFNULL((SELECT OFFM_TELNO FROM COMTNEMPLYRINFO B WHERE REG_ID = B.EMPLYR_ID ), '') AS REG_OFFM_TELNO
, fn_getMemberNm(REG_ID) AS REG_NM
, REG_DT FROM CMS_CONT_BRD WHERE CMS_CD = #{cmsCd}
AND UP_BRD_NO = #{brdNo}
AND BRD_NO = #{brdNo}

mapper.insert:

	SELECT IFNULL(MAX(BRD_NO),0)+1 AS brdNo, 
	CONCAT('${mType}','_','${cmsCd}','_',LPAD(IFNULL(MAX(BRD_NO),0)+1,10,0)) AS attachId								
	FROM CMS_CONT_BRD			
	WHERE CMS_CD = #{cmsCd}


INSERT INTO  CMS_CONT_BRD  ( 
	  CMS_CD 
	 ,BRD_NO 
	 ,TITLE 
	 ,CONT 
	 ,HIT 
	 ,DEP 
	 ,DEL_YN 
	 ,SECRET_YN 
	 ,REP_YN 
	 ,ATTACH_ID 
	 ,IPS 
	 ,REG_ID 
	 ,REG_DT 
 ) VALUES ( 
	  #{cmsCd} 
	 ,#{brdNo} 
	 ,#{title} 
	 ,#{cont} 
	 ,0
	 ,1 
	 ,'N' 
	 ,#{secretYn} 
	 ,'N'
	 ,#{attachId} 
	 ,#{ips} 
	 ,#{regId} 
	 ,SYSDATE()
);

mapper.update:
UPDATE

	CMS_CONT_BRD

SET
	DEL_YN = 'Y'		   				
WHERE CMS_CD = #{cmsCd}
AND BRD_NO = #{brdNo}
