mapper.select:
SELECT COMCD , GRP_CD , CD , CD_NM , SORT_ORDER , REMARK , ITEM1 , ITEM2 , ITEM3 , ITEM4 , ITEM5 , UPD_YN , DEL_YN , USE_YN , REC_SDATE , REC_EDATE , REP_SDATE , REP_EDATE , STARTDATE FROM COT_GRPCD
AND COMCD = #{comcd}
AND ITEM1 = #{item1}
AND GRP_CD = 'CM_MCLASS_CD' AND USE_YN = 'Y'
ORDER BY SORT_ORDER

SELECT R1.* FROM ( SELECT A.COMCD , A.CLASS_CD , A.USER_NO , CASE A.USER_NO WHEN 'NOASSIGN' THEN '미지정' ELSE Fn_GetEmpCodeNm(A.USER_NO) END USER_NM , A.PART_CD , Fn_GetGrpCodeNm(A.COMCD, 'SM_PART_TYPE', A.PART_CD) AS PART_CD_NM , A.SPORTS_CD , Fn_GetGrpCodeNm(A.COMCD, 'CM_SPORTS_CD', A.SPORTS_CD) AS SPORTS_CD_NM , A.MSPORTS_CD , Fn_GetGrpCodeNm(A.COMCD, 'CM_MCLASS_CD', A.MSPORTS_CD) AS MSPORTS_CD_NM , A.CLASS_NM , A.PLACE_CD , Fn_GetGrpCodeNm(A.COMCD, 'CM_CLPLACE_CD', A.PLACE_CD) AS PLACE_CD_NM , A.GRADE_CD , Fn_GetGrpCodeNm(A.COMCD, 'CM_GRADE_CD', A.GRADE_CD) AS GRADE_CD_NM , A.PROGRESS_CD , Fn_GetGrpCodeNm(A.COMCD, 'CM_PROGRESS_CD', A.PROGRESS_CD) AS PROGRESS_CD_NM , A.STYLE , Fn_GetGrpCodeNm(A.COMCD, 'CM_STYLE_CD', A.STYLE) AS STYLE_NM , A.TRAIN_DAY , Fn_GetUseDayNm(A.TRAIN_DAY) AS TRAIN_DAY_NM , A.TRAIN_STIME , Fn_GetTimeFormat(A.TRAIN_STIME) AS TRAIN_STIME_NM , A.TRAIN_ETIME , Fn_GetTimeFormat(A.TRAIN_ETIME) AS TRAIN_ETIME_NM , CONCAT(Fn_GetTimeFormat(A.TRAIN_STIME), '~', Fn_GetTimeFormat(A.TRAIN_ETIME)) AS TRAIN_TIME_NM , A.CS_CAPA , A.WEB_CAPA , A.CAPA , A.CLASS_DESC , A.CLASS_NOTE , A.CLASS_READY , A.MAT_COST , A.MAT_TEACH , A.BIGO , A.CLASS_OBJ , A.CLASS_SOBJ , A.CLASS_EOBJ , A.CHILD_YN , A.DCRATE_YN , A.WEB_DISPLAY , A.SORT_ORDER , A.USE_YN , CASE A.USE_YN WHEN 'Y' THEN '사용' ELSE '미사용' END USE_YN_NM , A.CLASS_PHOTO , A.NEW_CHECK , A.TDATE_USE_YN , A.TC_SDATE , A.TC_EDATE , ( SELECT COUNT(1) FROM ITEM_CLASS B , PROGRAM_ITEM C WHERE A.COMCD = B.COMCD AND B.COMCD = C.COMCD AND A.CLASS_CD = B.CLASS_CD AND B.ITEM_CD = C.ITEM_CD AND C.USE_YN = 'Y' ) AS PRG_COUNT FROM TRAIN_CLASS A WHERE USE_YN = 'Y' AND WEB_DISPLAY = 'Y' ) R1
AND R1.COMCD = #{comcd}
AND R1.SPORTS_CD = #{sportsCd}
AND R1.MSPORTS_CD = #{msportsCd}
LIMIT #{startRow}, #{pageSize}

    	   SELECT COUNT(1)
		     FROM TRAIN_CLASS
		    WHERE USE_YN = 'Y'
		      AND WEB_DISPLAY = 'Y'

		      AND COMCD = #{comcd}


		      AND SPORTS_CD = #{sportsCd}


		      AND MSPORTS_CD = #{msportsCd}

mapper.insert:
INSERT INTO  MEM_CART SET 
	  COMCD 	=	 #{COMCD} 
	 ,ITM_TYPE 	=	 #{ITM_TYPE} 
	 ,PART_CD 	=	 #{PART_CD} 
	 ,SPORTS_CD 	=	 #{SPORTS_CD}
	 ,CLASS_CD 	=	 #{CLASS_CD}
	 ,ITEM_CD 	=	 #{ITEM_CD} 
	 ,MEM_NO 	=	 #{MEM_NO} 
	 ,ITEM_SDATE 	=	 #{ITEM_SDATE} 
	 ,ITEM_EDATE 	=	 #{ITEM_EDATE} 
	 ,DC_CD1 	=	 #{DC_CD1} 
	 ,DC_CD2 	=	 #{DC_CD2} 
	 ,SALE_AMT 	=	 #{SALE_AMT} 
	 ,ADD_YMD 	=	 #{ADD_YMD} 
	 ,WRITE_DH 	=	 #{WRITE_DH} 
ON DUPLICATE 
 KEY UPDATE 
	  COMCD 	=	 #{COMCD} 
	 ,ITM_TYPE 	=	 #{ITM_TYPE} 
	 ,PART_CD 	=	 #{PART_CD} 
	 ,SPORTS_CD 	=	 #{SPORTS_CD}
	 ,CLASS_CD 	=	 #{CLASS_CD} 
	 ,ITEM_CD 	=	 #{ITEM_CD} 
	 ,MEM_NO 	=	 #{MEM_NO} 
	 ,ITEM_SDATE 	=	 #{ITEM_SDATE} 
	 ,ITEM_EDATE 	=	 #{ITEM_EDATE} 
	 ,DC_CD1 	=	 #{DC_CD1} 
	 ,DC_CD2 	=	 #{DC_CD2} 
	 ,SALE_AMT 	=	 #{SALE_AMT} 
	 ,ADD_YMD 	=	 #{ADD_YMD} 
	 ,WRITE_DH 	=	 #{WRITE_DH}
