mapper.select:
SELECT COUNT(1)
  FROM MEMBER_DAMO
 WHERE STATUS = '00'
   AND MEM_NM = #{memNm}
   AND SEC_BIRTH_DATE = ENC_STR('KEY1', #{secBirthDate})
   AND GENDER = #{gender}

   AND HP = #{hp}

SELECT COUNT(1)
  FROM MEMBER_DAMO
 WHERE ID = #{id}

mapper.update:
UPDATE MEMBER_DAMO
   SET ID = #{id}
     , PW = #{pw}
     , BIRTH_SEC = #{birthSec}
     , EMAIL = #{email}
     , HOME_ZIP = #{homeZip}
     , HOME_ADDR = #{homeAddr}
     , HOME_TEL = #{homeTel}
     , EMAIL_YN = #{emailYn}
     , SMS_YN = #{smsYn}
 WHERE MEM_NM = #{memNm}
   AND SEC_BIRTH_DATE = ENC_STR('KEY1', #{secBirthDate})
   AND GENDER = #{gender}
   AND HP = #{hp}

UPDATE MEMBER_DAMO
   SET PW = #{pw}
 WHERE MEM_NM = #{memNm}
   AND SEC_BIRTH_DATE = ENC_STR('KEY1', #{secBirthDate})
   AND GENDER = #{gender}
   AND HP = #{hp}
