mapper.select:
select t1.COMCD, t1.PLACE_GROUP, t2.COMNM from rent_place t1 left join cominfo t2 on t1.COMCD = t2.COMCD where t1.USE_YN = 1
AND t1.PLACE_GROUP = #{PLACE_GROUP}
group by t1.COMCD, t1.PLACE_GROUP, t2.COMNM order by t1.COMCD

select t1.COMCD , t2.COMNM , t1.PLACE_CD , t1.PLACE_GROUP , t1.PLACE_NM , t1.PART_CD , t1.TIME_UNIT , t1.RENT_AMT , t1.info_url , t1.USE_YN FROM rent_place t1 left join cominfo t2 on t1.COMCD = t2.COMCD WHERE t1.USE_YN=1
AND t1.PLACE_GROUP = #{PLACE_GROUP}
AND t1.COMCD = #{COMCD}
Order by t1.COMCD

mapper.insert:
INSERT INTO rent_team  SET 
	 mem_id 	=	 #{mem_id} 
	 ,sp_type 	=	 #{sp_type} 
	 ,tm_type 	=	 #{tm_type} 
	 ,tm_nm 	=	 #{tm_nm} 
	 ,wdate 	=	 now()

ON DUPLICATE 
KEY UPDATE 
	 sp_type 	=	 #{sp_type} 
	 ,tm_type 	=	 #{tm_type} 
	 ,tm_nm 	=	 #{tm_nm} 
	 ,wdate 	=	 now()


select seq from rent_team where mem_id= #{mem_id}  order by seq desc limit 1

INSERT INTO  rent_team_mem  SET 
	 team_seq 	=	 #{team_seq} 
	 ,mem_nm 	=	 #{mem_nm} 
	 ,mem_birth 	=	 #{mem_birth} 
	 ,mem_addr 	=	 #{mem_addr} 
	 ,mem_addr2 	=	 #{mem_addr2} 

ON DUPLICATE 
 KEY UPDATE 
	 team_seq 	=	 #{team_seq} 
	 ,mem_nm 	=	 #{mem_nm} 
	 ,mem_birth 	=	 #{mem_birth} 
	 ,mem_addr 	=	 #{mem_addr} 
	 ,mem_addr2 	=	 #{mem_addr2}

mapper.select:
select
	count(*)
from
	rent_app
where
	RESERVE_DATE = #{RESERVE_DATE}
	and PLACE_CD = #{PLACE_CD}
	and PLACE_TIME_SEQ = #{TIME_SEQ}
	and APP_TYPE != 40

select t1.seq, t1.place_cd, t1.place_tab, t2.PLACE_NM , t1.item, ifnull(t3.RENT_IDX, 0) as rentIdx, ifnull(t3.RESERVE_DATE,0) as RESERVE_DATE, case t3.APP_TYPE when '10' then '접수대기' when '20' then '확인중' when '30' then '확정' when '40' then '취소' else '' end as APP_TYPE_NM, ifnull(t3.BOSSNM,'') as BOSSNM from rent_times t1 left join rent_place t2 on t1.place_cd = t2.PLACE_CD left join rent_app t3 on t1.place_cd = t3.PLACE_CD
and left(t3.RESERVE_DATE, 6) = #{YYMM}
and t3.RESERVE_DATE = #{YYMMDD}
and t3.PLACE_TAB = #{PLACE_TAB}
and t1.seq = t3.PLACE_TIME_SEQ
and t3.APP_TYPE != 40 where t2.PLACE_CD = #{PLACE_CD} and t1.use_yn =1 order by t1.place_tab, t1.sort, t1.seq
