mapper.select:
select t1.COMCD, t1.PLACE_GROUP, t2.COMNM from rent_place t1 left join cominfo t2 on t1.COMCD = t2.COMCD where t1.USE_YN = 1
AND t1.PLACE_GROUP = #{PLACE_GROUP}
group by t1.COMCD, t1.PLACE_GROUP, t2.COMNM order by t1.COMCD

select t1.COMCD , t2.COMNM , t1.PLACE_CD , t1.PLACE_GROUP , t1.PLACE_NM , t1.PART_CD , t1.TIME_UNIT , t1.RENT_AMT , t1.info_url , t1.USE_YN FROM rent_place t1 left join cominfo t2 on t1.COMCD = t2.COMCD WHERE t1.USE_YN=1
AND t1.PLACE_GROUP = #{PLACE_GROUP}
AND t1.COMCD = #{COMCD}
Order by t1.COMCD

mapper.insert:
INSERT INTO rent_team  SET 
	 mem_id 	=	 #{mem_id} 
	 ,sp_type 	=	 #{sp_type} 
	 ,tm_type 	=	 #{tm_type} 
	 ,tm_nm 	=	 #{tm_nm} 
	 ,wdate 	=	 now()

ON DUPLICATE 
KEY UPDATE 
	 sp_type 	=	 #{sp_type} 
	 ,tm_type 	=	 #{tm_type} 
	 ,tm_nm 	=	 #{tm_nm} 
	 ,wdate 	=	 now()


select seq from rent_team where mem_id= #{mem_id}  order by seq desc limit 1

